(7364, 'en', 'LayerSlider', 'd2157b3302d4ec2fb08023a69d4568fd', 'Enable this option to allow reverse transition, so you can loop back and forth seamlessly.', NULL, 'LINE', NULL, 0, '', '0ea798ec5540aede500d2b38c21134e0'),
(7365, 'en', 'LayerSlider', '0211cac74bd72aaae2f7886b1cbde16f', 'Moves the layer horizontally by the given number of pixels. Use negative values for the opposite direction. Percentage values are relative to the width of this layer. ', NULL, 'LINE', NULL, 0, '', '3f45a05684b59184a420abc5d8be46f9'),
