(7377, 'en', 'LayerSlider', '7b57923badae7705a941c2bb5fdbc259', 'Always on top', NULL, 'LINE', NULL, 0, '', '5b0b8400c6419fed1590fa5b18928d44'),
(7378, 'en', 'LayerSlider', '97309a236d6310d21531928e0f237909', 'Show this layer above every other layer while hovering.', NULL, 'LINE', NULL, 0, '', 'a05bec4fd63e7a6a8b691d3dcc0630c5'),
(7379, 'en', 'LayerSlider', 'b437c451aa3282f75304078beaccd7f2', 'Parallax Level', NULL, 'LINE', NULL, 0, '', '7ddd1f9a046c8624964e01007aa6040f'),
(7380, 'en', 'LayerSlider', '6d88cda2a17e8c8ba371534044e4dcac', 'Set the intensity of the parallax effect. Use negative values to shift layers in the opposite direction.', NULL, 'LINE', NULL, 0, '', '316adf1c0b50658da1e1b2421ae1e040'),
(7381, 'en', 'LayerSlider', 'e32aeb1f97bbc4b3734bceba06e62189', 'Choose if you want 2D or 3D parallax layers.', NULL, 'LINE', NULL, 0, '', 'eb3b786eb0c99bc4a77fa8e514e86fe8'),
(7382, 'en', 'LayerSlider', 'db7e7d3165d15f05c446c312cd7ce748', 'Inherit from Slide Options', NULL, 'LINE', NULL, 0, '', '317dfaf2f62f8b45c99d6980859ab58f'),
(7383, 'en', 'LayerSlider', '65c19dcd436dba37e3f61abaa74fcb0a', 'You can trigger the parallax effect by either scrolling the page, or by moving your mouse cursor / tilting your mobile device.', NULL, 'LINE', NULL, 0, '', 'a8034638dc5a384b895a72a2284900f6'),
(7384, 'en', 'LayerSlider', 'df4937d57d5546a1b7cad8aa01382a30', 'Choose on which axes parallax layers should move.', NULL, 'LINE', NULL, 0, '', '3bcbd5de3338cc1b640048a0e88b448b'),
(7385, 'en', 'LayerSlider', '130c5b3473c57faa76e2a1c54e26f88e', 'Both', NULL, 'LINE', NULL, 0, '', 'a7718426b0b532741e191bc845d6e2dc'),
