(7387, 'en', 'LayerSlider', '902394e5c2f298dc004991c64ea639e0', 'Controls the speed of animating layers when you move your mouse cursor or tilt your mobile device.', NULL, 'LINE', NULL, 0, '', '8ac42c97af3ace5233f5a689caa85d74'),
(7388, 'en', 'LayerSlider', 'e6c62193f078181acd6cc60c21353d08', 'Leave Duration', NULL, 'LINE', NULL, 0, '', '569e75029bb7738f3dc0dfc562d3c8e4'),
(7389, 'en', 'LayerSlider', '387b79d99e6882187a0e3530f37a2f76', 'Controls how quickly parallax layers revert to their original position when you move your mouse cursor outside of the slider. This value is in milliseconds. A second equals to 1000 milliseconds.', NULL, 'LINE', NULL, 0, '', '2cb0eaea9dada3377062ec1d4af8b98f'),
(7390, 'en', 'LayerSlider', '64a0612acd329567a634da43b59883cf', 'Increase or decrease the amount of layer rotation in the 3D space when moving your mouse cursor or tilting on a mobile device.', NULL, 'LINE', NULL, 0, '', '06d79e4a8fb0349bdc2d384f02e96b0e'),
(7391, 'en', 'LayerSlider', 'fb86197f4f68b1e2d86783659e7585a3', 'Increase or decrease the amount of layer movement when moving your mouse cursor or tilting on a mobile device.', NULL, 'LINE', NULL, 0, '', '3bcd8aaa74ca8fce3baafc8d7012a71d'),
(7392, 'en', 'LayerSlider', '60fc3929244c72dfa7a991d00b3430c8', 'Keep this layer visible:', NULL, 'LINE', NULL, 0, '', '9835b87b15069ce250a473a11d273127'),
(7393, 'en', 'LayerSlider', '9ae2250c53514362ad6490c3431e5464', 'You can keep this layer on top of the slider across multiple slides. Just select the slide on which this layer should animate out. Alternatively, you can make this layer global on all slides after it transitioned in.', NULL, 'LINE', NULL, 0, '', '3b62535be509de42282180515269c9f1'),
