(7397, 'en', 'LayerSlider', '8e898860158d07998968bfd485788069', 'A Play by Scroll slider will pause when this layer finished its opening transition.', NULL, 'LINE', NULL, 0, '', '10126e1a8c69990dbef51d6bb6d6367c'),
(7398, 'en', 'LayerSlider', '627b4c0e8a9b46747497d4bae491a6c2', 'If you want to link your layer, type here the URL. You can use a hash mark followed by a number to link this layer to another slide. Example: #3 - this will switch to the third slide.', NULL, 'LINE', NULL, 0, '', '8180ff1fac9ef1e23fd366387b2c2baa'),
(7399, 'en', 'LayerSlider', 'a33a3e8542755c06996a81a78878b66b', 'URL target', NULL, 'LINE', NULL, 0, '', '347ee170671ff19815e778bf8c42de1e'),
(7400, 'en', 'LayerSlider', 'ba333933886b07c9384523873559a3fd', 'Custom Attributes', NULL, 'LINE', NULL, 0, '', '86b03a3e3ebd7b1dafa0cc90897a6aa3'),
